mapper.select:
select

from organization_members om
where
om.organization_uuid = #{organizationUuid, jdbcType=VARCHAR}
and om.user_id = #{userId, jdbcType=INTEGER}

select om.user_id
from organization_members om
where om.organization_uuid=#{organizationUuid,jdbcType=VARCHAR}

select om.organization_uuid as "organizationUuid"
from organization_members om
where om.user_id = #{userId, jdbcType=INTEGER}

select om.organization_uuid as "organizationUuid", count(om.user_id) as "memberCount" from organization_members om where om.organization_uuid in
#{organizationUuid, jdbcType=VARCHAR}
group by om.organization_uuid

mapper.insert:
insert into organization_members
(
organization_uuid,
user_id
)
values
(
#{organizationUuid, jdbcType=VARCHAR},
#{userId, jdbcType=INTEGER}
)

mapper.delete:
delete from organization_members
where
organization_uuid = #{organizationUuid, jdbcType=VARCHAR}
and user_id = #{userId, jdbcType=INTEGER}

delete from organization_members
where
organization_uuid = #{organizationUuid, jdbcType=VARCHAR}
