mapper.select:
select u.login from properties p inner join users u on p.user_id = u.id
left outer join projects p1 on p.resource_id=p1.id and p1.kee=#{componentKey,jdbcType=VARCHAR}
where p.prop_key = #{propKey,jdbcType=VARCHAR} and p.text_value like 'true' and ( p.resource_id is null
or p.resource_id=p1.id
)

