  prop_key=#{key}


    and resource_id=#{componentId}
    and user_id=#{userId,jdbcType=INTEGER}


    and resource_id=#{componentId}
    and user_id is null


    and resource_id is null
    and user_id=#{userId,jdbcType=INTEGER}


    and resource_id is null
    and user_id is null

